        $cliente=$this->db->query($sql)->row_array();
        $sql1 = "SELECT inscripcion_id FROM inscripcion WHERE cliente_id=".$cliente['cliente_id']." and date(inscripcion_fechaini) <= date(now()) and date(inscripcion_fechafin) >= date(now()) ";
        $inscripcion=$this->db->query($sql1)->row_array();
        //$acceso = "SELECT * FROM acceso WHERE inscripcion_id=".$inscripcion['inscripcion_id']." and acceso_ingreso=date(now()) ";
        $params = array(
                'cliente_id' => $cliente['cliente_id'],
                'inscripcion_id' => $inscripcion['inscripcion_id'],
                'acceso_estado' => 'INGRESO',
                //'acceso_ingreso' => $this->input->post('acceso_ingreso'),
                //'acceso_salida' => $this->input->post('acceso_salida'),
            );
            
            $acceso_id = $this->Acceso_model->add_acceso($params);
            echo json_encode($inscripcion);
           // redirect('acceso/index');
            }
    }

    function buscarfecha()
