    function add()
    {   
        if(isset($_POST) && count($_POST) > 0)     
        {   
            $params = array(
				'cliente_id' => $this->input->post('cliente_id'),
				'inscripcion_id' => $this->input->post('inscripcion_id'),
				'acceso_estado' => $this->input->post('acceso_estado'),
				'acceso_ingreso' => $this->input->post('acceso_ingreso'),
				'acceso_salida' => $this->input->post('acceso_salida'),
            );
            
            $acceso_id = $this->Acceso_model->add_acceso($params);
            redirect('acceso/index');
        }
        else
        {            
            $data['_view'] = 'acceso/add';
            $this->load->view('layouts/main',$data);
        }
    }  

    /*
     * Editing a acceso
     */
    function edit($acceso_id)
    {   
        // check if the acceso exists before trying to edit it
        $data['acceso'] = $this->Acceso_model->get_acceso($acceso_id);
        
        if(isset($data['acceso']['acceso_id']))
        {
            if(isset($_POST) && count($_POST) > 0)     
            {   
                $params = array(
					'cliente_id' => $this->input->post('cliente_id'),
					'inscripcion_id' => $this->input->post('inscripcion_id'),
					'acceso_estado' => $this->input->post('acceso_estado'),
					'acceso_ingreso' => $this->input->post('acceso_ingreso'),
					'acceso_salida' => $this->input->post('acceso_salida'),
                );

                $this->Acceso_model->update_acceso($acceso_id,$params);            
                redirect('acceso/index');
            }
            else
            {
                $data['_view'] = 'acceso/edit';
                $this->load->view('layouts/main',$data);
            }
        }
        else
            show_error('The acceso you are trying to edit does not exist.');
    } 

    /*
     * Deleting acceso
     */
    function remove($acceso_id)
    {
        $acceso = $this->Acceso_model->get_acceso($acceso_id);

        // check if the acceso exists before trying to delete it
        if(isset($acceso['acceso_id']))
        {
            $this->Acceso_model->delete_acceso($acceso_id);
            redirect('acceso/index');
        }
        else
            show_error('The acceso you are trying to delete does not exist.');
    }
    
}
